// src/pages/TeacherClassSelect.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function TeacherClassSelect() {
  const [classes, setClasses]   = useState([]);
  const [cls, setCls]           = useState('');
  const [section, setSection]   = useState('');
  const [error, setError]       = useState('');
  const navigate                = useNavigate();

  useEffect(() => {
    axios.get('/api/teacher/classes', {
      headers: { Authorization: `Bearer ${localStorage.getItem('teacherToken')}` }
    })
    .then(res => setClasses(res.data.assignedClasses))
    .catch(err => {
      console.error('Error fetching classes:', err);
      setError(err.response?.data?.message || 'Failed to load classes');
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!cls || !section) {
      setError('Please select both class and section');
      return;
    }
    navigate('/teacher/dashboard', { state: { cls, section } });
  };

  return (
    <div className="dashboard">
      <h2>Select Class and Section</h2>
      {error && <p className="error">{error}</p>}
      <form className="post-form" onSubmit={handleSubmit}>
        <select value={cls} onChange={e => setCls(e.target.value)}>
          <option value="">Select Class</option>
          {classes.map(c => (
            <option key={c.class} value={c.class}>{c.class}</option>
          ))}
        </select>
        <select value={section} onChange={e => setSection(e.target.value)}>
          <option value="">Select Section</option>
          {classes
            .filter(c => c.class === cls)
            .map(c => (
              <option key={c.section} value={c.section}>{c.section}</option>
            ))
          }
        </select>
        <button type="submit">Continue</button>
      </form>
    </div>
  );
}
